function scrollToTop(){const e=document.documentElement;e.scrollTo({top:0}),e.focus()}const scrollToTopButton=document.getElementById("scroll-to-top");scrollToTopButton.addEventListener("click",scrollToTop);const hasChildren=document.getElementsByClassName("menu-item-has-children");function showSubMenu(){const e=this.querySelector(".sub-menu");e.classList.add("axel-submenu--show"),e.style.display="block"}function hideSubMenu(){const e=this.querySelector(".sub-menu");e.classList.remove("axel-submenu--show"),e.style.display="none"}Array.from(hasChildren).forEach(e=>{e.addEventListener("mouseover",showSubMenu),e.addEventListener("mouseout",hideSubMenu)});