function showSubMenu(){this.querySelector(".axel-submenu").classList.add("axel-submenu--show")}function hideSubMenu(){this.querySelector(".axel-submenu").classList.remove("axel-submenu--show")}const hasChildren=document.getElementsByClassName("axel-menu__has-children");hasChildren&&Array.from(hasChildren).forEach(e=>{e.addEventListener("mouseover",showSubMenu),e.addEventListener("mouseout",hideSubMenu)});